import streamlit as st
import pandas as pd
from sklearn.ensemble import RandomForestRegressor
import numpy as np
from PIL import Image
import random

# Load dataset
data = pd.read_csv('credit_score_dataset_200.csv')

# Build the RandomForestRegressor model
X = data[['Mobile Data Usage (GB)', 'Call Records', 'Utility Payment (Months On-Time)', 
          'Transaction Count', 'Social Media Activity (Posts)',
          'Income Level (Monthly)', 'Loan Repayment History (Loans Paid)', 
          'Credit Cards Owned', 'Shopping Behavior (Monthly Spend)']]
y = data['Credit Score']
model = RandomForestRegressor()
model.fit(X, y)

# Streamlit UI
st.title("AI-Driven Credit Scoring for Financial Inclusion")


# Adding Images for visual appeal
image_urls = [
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/jpeg;base64,",
    "data:image/png;base64,"
]
st.image(random.choice(image_urls), use_column_width=True)

st.subheader("Enter Your Data for Credit Score Prediction")

# Input Fields
mobile_usage = st.number_input("Mobile Data Usage (GB)", min_value=0)
call_records = st.number_input("Call Records (Number of Calls)")
utility_payments = st.number_input("Utility Payment (Months On-Time)", min_value=0)
transaction_count = st.number_input("Transaction Count")
social_media = st.number_input("Social Media Activity (Posts)", min_value=0)
income_level = st.number_input("Income Level (Monthly)", min_value=0)
loan_history = st.number_input("Loan Repayment History (Loans Paid)", min_value=0)
credit_cards = st.number_input("Credit Cards Owned", min_value=0)
shopping_spend = st.number_input("Shopping Behavior (Monthly Spend)", min_value=0)

# Document Verification Section
st.subheader("Document Verification (Optional)")
uploaded_docs = st.file_uploader("Upload Your ID Documents (PDFs or Images)", type=["pdf", "png", "jpg"])

if uploaded_docs:
    st.success("Documents uploaded successfully! Verification pending.")

# Prediction
if st.button('Predict Credit Score'):
    input_data = np.array([[mobile_usage, call_records, utility_payments, transaction_count, 
                            social_media, income_level, loan_history, 
                            credit_cards, shopping_spend]])
    predicted_score = model.predict(input_data)[0]
    st.write(f"Predicted Credit Score: {predicted_score:.2f}")
    
    # Display Fraud Detection if values are too abnormal
    if mobile_usage > 100 or call_records > 1000 or income_level > 20000:
        st.warning("Suspicious activity detected! Please review your inputs.")
    else:
        st.success("Prediction successful!")

# Educational Section: Financial Tips
st.sidebar.title("Financial Education")
st.sidebar.write("Tips to Improve Your Credit Score")
st.sidebar.write("- Pay your utility bills on time.")
st.sidebar.write("- Keep your credit card usage below 30% of your limit.")
st.sidebar.write("- Avoid applying for too many credit cards at once.")
st.sidebar.write("- Pay off your loan repayments as quickly as possible.")

# Additional Sidebar Content: Educational Videos & Resources
st.sidebar.subheader("Learn More About Credit Scoring")
st.sidebar.write("Watch this video to understand how credit scores are calculated:")
st.sidebar.video("https://www.youtube.com/watch?v=29UswuonF4M")

st.sidebar.write("Read more about improving your credit score:")
st.sidebar.write("[Credit Score Improvement Guide](https://www.credit.com/credit-scores/how-to-improve-your-credit-score/)")

# Educational Images to enhance experience
st.sidebar.image(random.choice(image_urls), use_column_width=True)

# Footer with more tips
