# Input for additional alternative data (Example: Mobile payments, utility bill payments)
mobile_payments = st.number_input("Mobile Payments (Monthly Total in $)", min_value=0)
utility_bills = st.number_input("Utility Bill Payments (Monthly Total in $)", min_value=0)
online_behavior = st.number_input("Online Behavior (Hours Spent on Financial Platforms)", min_value=0)

