if st.button('Predict Credit Score with Alternative Data'):
    input_data_alternative = np.array([[mobile_usage, call_records, utility_payments, 
                                        transaction_count, social_media, income_level, 
                                        loan_history, credit_cards, shopping_spend, 
                                        mobile_payments, utility_bills, online_behavior]])
    
    predicted_score_alternative = model.predict(input_data_alternative)[0]
    st.write(f"Predicted Credit Score with Alternative Data: {predicted_score_alternative:.2f}")

# 2. Risk Assessment and Fraud Prevention
